import styled from "styled-components";
import { Container as C, Button as B } from "../../globalStyles";
export const Row = styled.div`
  text-align: ${({ align }) => !align && "left"};
  gap: ${({ sps }) => sps && `${sps}rem`};
  display: flex;
  flex-direction: column;
  align-items:${({})}
  border: ${({ border }) => border && "1px solid var(--black)"};
`;
export const RowImg = styled.div`
  width: 400px;
  height: 500px;
  img {
    width: 100%;
    height: 100%;
  }
`;
export const Container = styled(C)`
  display: flex;

  flex-direction: ${({ rev }) => (rev ? "row-reverse" : "row")};
  width: 100%;
  justify-content: ${({ center }) => (center ? "center" : "space-between")};
  align-items: center;
  color: var(--primary);
  h2 {
    font-size: 60px;
    font-weight: 700;
  }
  img {
    width: ${({ fit }) => fit && "fit-content"};
  }
  p {
    font-size: ${({ smallP }) => (smallP ? "16px" : "30px")};
    font-weight: 100;
    width: 80%;
  }
  .btns {
    display: flex;
    gap: 1rem;
  }
`;
export const Button = styled(B)`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: #eeeaff;
  column-gap: 1rem;
  padding: 0.5rem 1.5rem;

  img {
    width: 30px;
  }
  p {
    font-size: 16px;
    width: 100%;
  }
  p:last-of-type {
    color: ${({ google }) => (google ? `var(--green)` : `var(--purple)`)};
    font-size: 20px;
  }
  /* width: 40px;
  height: 20px; */
`;
