import React, { useState } from "react";
import "./styles.css";
function Square({ i, value, onBtnClick }) {
  return (
    <button onClick={() => onBtnClick(value)} value={value}>
      {i}
    </button>
  );
}
function App() {
  let square = Array(9).fill(null);
  const [state, setState] = useState({ squares: square, isNext: true });
  const [status, setStatus] = useState(`Player: ${state.isNext ? "X" : "O"}`);
  function onBtnClick(i) {
    const square = state.squares.slice();
    let temp = square[i];
    square[i] = state.isNext ? "X" : "O";
    square[i] = temp;

    if (calculateWinner(square)) {
      setStatus(`Player: ${calculateWinner(square)} Win`);
      return;
    }
    square[i] = state.isNext ? "X" : "O";
    setState({ squares: square, isNext: !state.isNext });
    setStatus(`Player: ${!state.isNext ? "X" : "O"}`);
  }
  return (
    <div className="container">
      <p>{status}</p>
      <div className="row">
        {square.map((e, i) => (
          <Square
            onBtnClick={onBtnClick}
            key={i}
            value={i}
            i={state.squares[i]}
          ></Square>
        ))}
      </div>
    </div>
  );
}
function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[b];
    }
  }
}

export default App;
